    <script type="text/javascript">
        function showDeclineNotePopUp(){
            $('#decline_with_note').modal('show');
        }
        function addDeclineNote(){
            var title = $('#die_title').val();
            var note = $('#die_note').val();
            var orderNumber =  $('#number').val();

            if(title == null || title ==""){
                show_faded_alert('die_title','please Add Title');
                return false;
            }

            if(note == null || note ==""){
                show_faded_alert('die_note','please Add Note');
                return false;
            }

            $.ajax({

                url: mainUrl+'order_quotation/Order/addDeclineNote',

                type:"POST",

                async:"false",

                dataType: "html",

                data: {title:title,note:note,orderNumber:orderNumber},

                success: function(data){
                    swal('Note is Added..')
                    $('#decline_with_note').modal('hide');

                }
            });
        }
        $(document).ready(function() {
            $(".globe-map").aaZoom();
            $().UItoTop({ easingType: 'easeOutQuart' });


            $('input[type="text"]').keyup(function(evt){
                //upperFirstAll()
                //upperFirst()
                //upperCase()
                //lowerCase()
                var element = $(this).attr('id'); console.log(element );
                if(element=='email_valid' || element=='email' || element=='d_email' || element=='password' || element=='cpassword' || element=='captcha' || element=='vatnumber' || element=='PurchaseOrderNumber' ){}
                else if(element=='b_pcode' || element=='d_pcode'){ 	$(this).upperCase();}
                else if(typeof element !='undefined' ){ $(this).upperFirstAll(); }
            });



        });

        $(document).on("click", ".sweet-alert > p > .confirm", function(e) {
            dataLayer.push({'event': 'fireRemarketingTag', 'ecomm_pagetype' : 'Click on checkout'});
        });

    </script>




















